#!/usr/bin/python -tt

metfile = open("LowCostMetabolites.txt", "r")
tfile = open("TransportDB.txt", "w")

for line in metfile.readlines():
  if '#' in line:
    continue
  line.rstrip()
  line = line.split('\t')
  metname = line[0].rstrip('\[e\]')
  if ('y' in line[3]) and ('y' in line[4]):
    tfile.write("Transport Database\t.\tR_%st\ttrue\t%stransport\t%s[c] <==> %s[e]\n" % (metname.replace("-","_"),metname,metname,metname))
  elif 'y' in line[3]:
    tfile.write("Transport Database\t.\tR_%st\ttrue\t%stransport\t%s[e] --> %s[c]\n" % (metname.replace("-","_"),metname,metname,metname))
  elif 'y' in line[4]:
    tfile.write("Transport Database\t.\tR_%st\ttrue\t%stransport\t%s[c] --> %s[e]\n" % (metname.replace("-","_"),metname,metname,metname))
    
  
  
